partials.account_sign_in:
{{> test_warning}}
<form id="sign-in-form" method="POST" class="sign-in-form">
  <div class="mdc-typography--headline5">
    {{#if @root.request.params.query.reauth ~}}
      Reauthenticate
    {{else}}
      Sign in with your Funding Scotland account
    {{/if ~}}
  </div>
  {{#if @root.request.params.query.reauth ~}}
  <div class="mdc-typography--headline6 v-margin">
    Before you can do that you must sign in again
  </div>
  {{/if ~}}
  {{#compare @root.data.currentSite.name "default" ~}}
  {{#>help_box id="sign-in-migration"}}
  Funding Scotland previously allowed sign in without a password, you now need to <a href="/reset-password?email={{urlencode @root.request.params.query.email}}">set a password</a>
  {{/help_box}}
  {{/compare ~}}
  <div class="mdc-card__info x-padding-full v-margin border rounded mdc-theme--error-light-bg mdc-theme--error-dark mdc-theme--error-border" id="sign-in-message">
    <span id="form-message"></span>
  </div>
  <div class="mdc-card__info">
    <div class="mdc-typography--body1" id="form-section-email">
      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-at mdc-text-field__icon" tabindex="-1"></span>
        <input type="email" id="sign-in-email" class="mdc-text-field__input mdc-text-field--fullwidth" value="{{@root.request.params.query.email}}" />
        <label class="mdc-floating-label" for="sign-in-email">
          Email
        </label>
        <div class="mdc-line-ripple"></div>
      </div>

      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-key mdc-text-field__icon" tabindex="-1"></span>
        <input type="password" id="sign-in-password" class="mdc-text-field__input mdc-text-field--fullwidth" />
        <label class="mdc-floating-label" for="sign-in-password">
          Password
        </label>
        <div class="mdc-line-ripple"></div>
      </div>
    </div>
    <div id="form-section-sign-in-buttons">
      <button class="mdc-button mdc-button--raised" type="submit" id="sign-in-submit">
        Sign in
      </button>
      <div class="mdc-typography--body1 v-margin-full">
        Forgot your password? <a href="/reset-password">Reset password</a>
      </div>
      <div class="mdc-typography--body1 v-margin-full">
        Don't have a Funding Scotland account? <a href="/sign-up">Sign up</a>
      </div>
      <div class="mdc-typography--body1 t-margin-full">
        Read our <a href="/privacy" target="_blank">privacy notice</a>
      </div>
    </div>
    <div class="card-loader" id="sign-in-loader">
      <div class="card-loader-inner">
        <span class="far fa-2x fa-spinner-third fa-spin"></span>
      </div>
    </div>
  </div>
</form>
<script>
document.addEventListener("DOMContentLoaded", function(event) {
  var $form = $('#sign-in-form');
  var $message = $('#sign-in-message');
  var $email = $('#sign-in-email');
  var $password = $('#sign-in-password');
  var $login = $('#sign-in-submit');
  var $loader = $('#sign-in-loader');

  $form.on('submit', function(evt) {
    var email = $email.val();
    var password = $password.val();
    $loader.css('display', 'flex');
    evt.preventDefault();

    fundingscotland.auth.signInEmailPassword(email, password)
      .then(function(user) {
        dataLayer.push({
          event: 'user-signed-in',
          uid: user.uid,
          site: window.currentSite
        });
        setTimeout(function() {
          window.location.href = '/signed-in?referer=' + encodeURIComponent('
            {{~#ifAny (contains @root.request.headers.referer "sign-out") (contains @root.request.headers.referer "reset-password") (contains @root.request.headers.referer "sign-in") (contains @root.request.headers.referer "sign-up")~}}
              /
            {{~else~}}
              {{default @root.request.headers.referer "/"}}
            {{~/ifAny~}}
          ');
        }, 1000);
      })
      .catch(function(err) {
        $message.html(err.message+' <a href="/reset-password">Reset password</a>').show();
        $loader.css('display', 'none');
      });
  });
});
</script>

partials.account_sign_out:
{{> account_sign_out_loader message="Signing out&hellip;"}}
<script>
document.addEventListener("DOMContentLoaded", function(event) {
  window.setTimeout(function() {
    fundingscotland.auth.sessionCleanup().then(function() {
      fundingscotland.setCookie('fs_token');
      fundingscotland.setCookie('fs_cookie');
      window.location.href = '/';
    }).catch(function(err) {
      console.error('Error signing out:', err);
    });
  }, 1000);
});
</script>

partials.account_sign_up:
{{#ifAll @root.data.auth (compare @root.route.name "sign_up")}}
<script>
document.addEventListener("DOMContentLoaded", function(event) {
  window.location.href = '/';
});
</script>
{{else}}
{{> test_warning}}
<form id="sign-up-form" method="POST" class="sign-in-form sign-up-form" autocomplete="off">
  <div class="mdc-typography--headline5">
    Sign up for a Funding Scotland account
  </div>
  <div class="mdc-card__info x-padding-full v-margin border rounded mdc-theme--error-light-bg mdc-theme--error-dark mdc-theme--error-border b-margin" id="sign-up-message">
    <span id="form-message"></span>
  </div>
  <div class="mdc-card__info">
    <div class="mdc-typography--body1" id="form-section-email">
      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-at mdc-text-field__icon" tabindex="-1"></span>
        <span class="far fa-spinner fa-spin mdc-text-field__icon hidden" id="sign-up-existing-spinner" tabindex="-1"></span>
        <input type="email" id="sign-up-email" class="mdc-text-field__input mdc-text-field--fullwidth" data-lpignore="true" autocomplete="off" required />
        <label class="mdc-floating-label" for="sign-up-email">
          Email
        </label>
        <div class="mdc-line-ripple"></div>
      </div>

      <div class="hidden mdc-theme--error-bg mdc-theme--on-error x-padding-full" id="sign-up-existing-message">
        You already have a Funding Scotland account with <span id="sign-up-existing-email"></span>
        which was most recently used on <a id="sign-up-existing-recent" class="mdc-theme--on-error"></a> <span id="sign-up-existing-current">this site</span> - you can <a href="/sign-in" id="sign-up-existing-sign-in" class="mdc-theme--on-error">sign in with this account</a> or <a href="/reset-password" id="sign-up-existing-reset" class="mdc-theme--on-error">reset your password</a>
      </div>

      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-at mdc-text-field__icon" tabindex="-1"></span>
        <input type="email" id="sign-up-email-confirm" class="mdc-text-field__input mdc-text-field--fullwidth" data-lpignore="true" autocomplete="off" required />
        <label class="mdc-floating-label" for="sign-up-email-confirm">
          Confirm email
        </label>
        <div class="mdc-line-ripple"></div>
      </div>

      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-key mdc-text-field__icon" tabindex="-1"></span>
        <input type="password" id="sign-up-password" class="mdc-text-field__input mdc-text-field--fullwidth" data-lpignore="true" autocomplete="off" required />
        <label class="mdc-floating-label" for="sign-up-password">
          Password
        </label>
        <div class="mdc-line-ripple"></div>
      </div>
      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-key mdc-text-field__icon" tabindex="-1"></span>
        <input type="password" id="sign-up-password-confirm" class="mdc-text-field__input mdc-text-field--fullwidth" data-lpignore="true" autocomplete="off" required />
        <label class="mdc-floating-label" for="sign-up-password-confirm">
          Confirm password
        </label>
        <div class="mdc-line-ripple"></div>
      </div>

      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-key mdc-text-field__icon" tabindex="-1"></span>
        <input type="text" id="sign-up-organisation" class="mdc-text-field__input mdc-text-field--fullwidth" data-lpignore="true" autocomplete="off" required />
        <label class="mdc-floating-label" for="sign-up-organisation">
          Organisation
        </label>
        <div class="mdc-line-ripple"></div>
      </div>
      <div class="mdc-text-field mdc-text-field--box mdc-text-field--with-leading-icon" data-mdc-auto-init="MDCTextField">
        <span class="far fa-key mdc-text-field__icon" tabindex="-1"></span>
        <input type="text" id="sign-up-postcode" class="mdc-text-field__input mdc-text-field--fullwidth" maxlength="9" data-lpignore="true" autocomplete="off" pattern="([Gg][Ii][Rr] 0[Aa]{2})|((([A-Za-z][0-9]{1,2})|(([A-Za-z][A-Ha-hJ-Yj-y][0-9]{1,2})|(([A-Za-z][0-9][A-Za-z])|([A-Za-z][A-Ha-hJ-Yj-y][0-9][A-Za-z]?))))\s?[0-9][A-Za-z]{2})" required />
        <label class="mdc-floating-label" for="sign-up-postcode">
          Postcode
        </label>
        <div class="mdc-line-ripple"></div>
      </div>

      <div class="mdc-form-field">
        <div class="mdc-checkbox">
          <input type="checkbox" class="mdc-checkbox__native-control" id="sign-up-subscribe"/>
          <div class="mdc-checkbox__background">
            <svg class="mdc-checkbox__checkmark" viewBox="0 0 24 24">
              <path class="mdc-checkbox__checkmark-path" fill="none" d="M1.73,12.91 8.1,19.28 22.79,4.59"/>
            </svg>
            <div class="mdc-checkbox__mixedmark"></div>
          </div>
        </div>
        <label for="sign-up-subscribe">Subscribe to the weekly bulletin</label>
      </div>
    </div>
    <div id="form-section-sign-up-buttons">
      <button class="mdc-button mdc-button--raised" type="submit" id="sign-up-submit">
        Sign up
      </button>
      <div class="mdc-typography--body1 v-margin-full">
        Already have a Funding Scotland account? <a href="/sign-in">Sign in</a>
      </div>
      <div class="mdc-typography--body1 t-margin-full">
        Read our <a href="/privacy" target="_blank">privacy notice</a>
      </div>
    </div>
    <div class="card-loader" id="sign-up-loader">
      <div class="card-loader-inner">
        <span class="far fa-2x fa-spinner-third fa-spin"></span>
      </div>
    </div>
  </div>
</form>
<script>
document.addEventListener("DOMContentLoaded", function(event) {
  var $form = $('#sign-up-form');
  var $message = $('#sign-up-message');
  var $emailForm = $('#email-password-form');
  var $email = $('#sign-up-email');
  var $emailConfirm = $('#sign-up-email-confirm');
  var $password = $('#sign-up-password');
  var $passwordConfirm = $('#sign-up-password-confirm');
  var $organisation = $('#sign-up-organisation');
  var $postcode = $('#sign-up-postcode');
  var $subscribe = $('#sign-up-subscribe');
  var $register = $('#sign-up-submit');
  var $loader = $('#sign-up-loader');

  $form.on('submit', function(evt) {
    $loader.css('display', 'flex');
    evt.preventDefault();

    const valid = evt.currentTarget.checkValidity();
    if (!valid) {
      evt.currentTarget.reportValidity();
      return;
    }

    var email = $email.val();
    var emailConfirm = $emailConfirm.val();
    var password = $password.val();
    var passwordConfirm = $passwordConfirm.val();

    fundingscotland.auth.createAccount(email, emailConfirm, password, passwordConfirm).then(function(user) {
      dataLayer.push({
        event: 'user-signed-up',
        uid: user.uid,
        site: window.currentSite
      });

      var redirect = '/new-user';
      var parameters = [];

      if ($subscribe.prop('checked')) {
        parameters.push('subscribe=true');
      }

      parameters.push('organisation=' + encodeURIComponent($organisation.val()));
      parameters.push('postcode=' + encodeURIComponent($postcode.val()));

      if (parameters.length > 0) {
        redirect += '?' + parameters.join('&');
      }

      setTimeout(function() {
        window.location.href = redirect;
      }, 1000);
    }).catch(function(err) {
      $message.text(err.message || 'Something went wrong when creating your account').show();
      $loader.css('display', 'none');
      document.body.scrollTop = document.documentElement.scrollTop = 0;
    });
  });

  $email.change(checkExisting);

  $existingSpinner = $('#sign-up-existing-spinner');
  $existingMessage = $('#sign-up-existing-message');
  $existingEmail = $('#sign-up-existing-email');
  $existingRecent = $('#sign-up-existing-recent');
  $existingCurrent = $('#sign-up-existing-current');
  $existingSignin = $('#sign-up-existing-sign-in');
  $existingReset = $('#sign-up-existing-reset');

  function checkExisting() {
    var email = $email.val().toLowerCase();
    var emailField = $email.parents('.mdc-text-field');
    emailField.addClass('mdc-text-field--with-trailing-icon');
    $existingSpinner.removeClass('hidden');
    $.getJSON('/existing-account?email=' + encodeURIComponent(email)).then(function(response) {
      emailField.removeClass('mdc-text-field--with-trailing-icon');
      $existingSpinner.addClass('hidden');
      if (response.profiles && response.profiles.length > 0) {
        response.profiles.sort(function (a, b){
          return a.accessed || 0 - b.accessed || 0;
        });
        lockExisting(email, response.profiles[0].title, response.profiles[0].url);
      } else {
        unlockExisting();
      }
    }).catch(function(err) {
      emailField.removeClass('mdc-text-field--with-trailing-icon');
      $existingSpinner.addClass('hidden');
      unlockExisting();
    });
  }

  function lockExisting(email, title, url) {
    $existingEmail.html(email);

    if (title === {{{stringify @root.data.currentSite.title}}}) {
      $existingCurrent.show();
      $existingRecent.hide();
    } else {
      $existingRecent.html(title);
      $existingRecent.attr('href', url);
      $existingCurrent.hide();
      $existingRecent.show();
    }

    $existingSignin.attr('href', '/sign-in?email=' + encodeURIComponent(email));
    $existingReset.attr('href', '/reset-password?email=' + encodeURIComponent(email));
    $existingMessage.removeClass('hidden');
    $email[0].setCustomValidity('Account already exists');
    $form[0].reportValidity();
  }

  function unlockExisting() {
    $existingMessage.addClass('hidden');
    $existingEmail.html('');
    $existingRecent.html('');
    $existingRecent.attr('href', '');
    $existingSignin.attr('href', '');
    $existingReset.attr('href', '');
    $email[0].setCustomValidity('');
    $form[0].reportValidity();
  }
});
</script>
{{/ifAll}}

partials.ajax_chip:
{{#with (parse (@partial-block))}}
  <div class="mdc-chip {{if ../on "mdc-chip--selected"}}" {{{extraAttributes}}} data-ajax-chip='{
      "onUrl": "{{../onUrl}}",
      "offUrl": "{{../offUrl}}",
      "onMethod": "{{default ../onMethod "GET"}}",
      "offMethod": "{{default ../offMethod "GET"}}",
      {{#if onData}}
        "onData": {{{stringify onData}}},
      {{/if}}
      {{#if offData}}
        "offData": {{{stringify offData}}},
      {{/if}}
      {{#if onClasses}}
      "onClasses": {{{stringify onClasses}}},
      {{/if}}
      {{#if offClasses}}
      "offClasses": {{{stringify offClasses}}},
      {{/if}}
      "onText": "{{../onText}}",
      "offText": "{{../offText}}"
    }'>
    <span class="{{../iconClasses}} mdc-chip__icon mdc-chip__icon--leading {{if ../on "mdc-chip__icon--leading-hidden"}}" aria-hidden="true"></span>
    <div class="mdc-chip__checkmark">
      <svg class="mdc-chip__checkmark-svg" viewBox="-2 -3 30 30">
        <path class="mdc-chip__checkmark-path" fill="none" stroke="#ffffff" d="M1.73,12.91 8.1,19.28 22.79,4.59"/>
      </svg>
    </div>
    <div class="mdc-chip__text noselect">{{if ../on ../onText else=../offText}}</div>
  </div>
{{/with}}

partials.components_anchored_menu:
{{#with (default menuId (concat "menu-" (random 1000 9999))) as |menuId|}}
<div class="mdc-menu-surface--anchor {{../anchorClasses}}">
  <button class="mdc-button {{../buttonClasses}}" id="{{menuId}}-trigger">
    {{#if ../imageUrl ~}}
      <img src="{{../imageUrl}}" class="{{../imageClasses}}" />
    {{/if}}
    {{#if ../iconClasses}}
      <span class="{{../iconClasses}}"></span>
    {{/if ~}}
    {{{../buttonLabel}}}
    {{#if ../arrowClasses}}
      <span class="{{../arrowClasses}}"></span>
    {{/if}}
  </button>
  <div class="mdc-menu mdc-menu-surface {{../menuClasses}}" tabindex="-1" id="{{menuId}}" data-menu-trigger="#{{menuId}}-trigger">
    <ul class="mdc-menu__items mdc-list {{../menuListClasses}}" role="menu" aria-hidden="true">
      {{> @partial-block}}
    </ul>
  </div>
</div>
{{/with}}

partials.components_horizontal_card_scroller:
{{#ifAll (compare (length items) "===" 0) @partial-block}}
  {{> @partial-block}}
{{else}}
  <div class="mdc-layout-grid equal-height {{outerClasses}}">
    {{#if (any items)}}
    <div class="scroll-button scroll-left {{scrollButtonClasses}} {{scrollButtonLeftClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollLeftIconClasses}}"></span>
    </div>
    <div class="scroll-button scroll-right {{scrollButtonClasses}} {{scrollButtonRightClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollRightIconClasses}}"></span>
    </div>
    {{/if}}
    <div class="mdc-layout-grid__inner scrolling-grid {{innerClasses}}">
      {{#if (any items)}}
        {{#each items}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone {{../itemClasses}}">
            {{> (lookup ../this 'itemsPartial')}}
          </div>
        {{/each}}
        {{#if moreUrl}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone latest-content-primary last-card {{moreClasses}}">
            <div class="mdc-card align-centre mdc-typography--body1 {{moreCardClasses}}">
              <a href="{{{moreUrl}}}">{{default moreText "More"}}</a>
            </div>
          </div>
        {{/if}}
      {{/if}}
    </div>
  </div>
{{/ifAll}}

partials.fund_search_results:
{{#withExtend this savedSearch=(if
  @root.data.auth
  (itemAt
    @root.data.searches.items.[0]
    (indexOf
      (pluck @root.data.searches.items.[0] "querystring")
      (querystringify (obj)
        keywords=@root.request.params.query.keywords
        activities=(sort @root.request.params.query.activities)
        beneficiaries=(sort @root.request.params.query.beneficiaries)
        geographical_areas_funded=(sort @root.request.params.query.geographical_areas_funded)
        type_of_cost=(sort @root.request.params.query.type_of_cost)
        type_of_funding=(sort @root.request.params.query.type_of_funding)
        source_of_fund=(sort @root.request.params.query.source_of_fund)
        miscellaneous=(sort @root.request.params.query.miscellaneous)
      )
    )
  )
  else=false
)}}
{{>localised_site_banner @root.request.params.querystring}}

{{#if savedSearch}}
  <h3 class="mdc-typography--headline6">
    <div class="pull-right">
      {{#> ajax_chip
        onUrl=(concat "/toggle-subscription?name=" (urlencode savedSearch.partition))
        offUrl=(concat "/toggle-subscription?name=" (urlencode savedSearch.partition))
        onText="Disable email updates on this search"
        offText="Get email updates on this search"
        iconClasses="fal fa-fw fa-times"
        on=savedSearch.partitionActive
      }}
      {
        "extraAttributes": "data-saved-search-name=\"{{../savedSearch.partition}}\"",
        "onClasses": {
          "[data-saved-search-name=\"{{../savedSearch.partition}}\"] .mdc-chip__icon": "mdc-chip__icon--leading-hidden"
        },
        "offClasses": {
          "[data-saved-search-name=\"{{../savedSearch.partition}}\"]": ""
        }
      }
      {{/ajax_chip}}
    </div>
    Your search&ensp;
    <span class="mdc-typography--body1">
      {{{savedSearch.partition}}}
    </span>
  </h3>
{{/if}}

{{> search_info paging=@root.data.search_data.detailed_results.pagination}}

{{#>premium_content}}
  {{#unless savedSearch}}
  <aside id="save-search-dialog" class="mdc-dialog" role="alertdialog" aria-labelledby="save-search-dialog-label" aria-describedby="save-search-dialog-description">
    <div class="mdc-dialog__container">
      <form action="/save-search{{> query_search}}" method="GET" class="mdc-dialog__surface">
        <header class="mdc-dialog__header">
          <h2 id="save-search-dialog-label" class="mdc-dialog__title">Give your search a name</h2>
        </header>
        <section id="save-search-dialog-description" class="mdc-dialog__content">
          <p>
            Search names may only contain letters, numbers, spaces, and the following special characters:
            <span class="inline-pre">- _ , .</span>
          </p>

          <div class="mdc-text-field mdc-text-field--box" data-mdc-auto-init="MDCTextField">
            <input type="text" id="saved-search-name" name="name" class="mdc-text-field__input"
                value="" maxlength="255" data-restricted="[\w\d\s\-_.,]+">
            <label for="saved-search-name" class="mdc-floating-label">Save search</label>
            <div class="mdc-line-ripple"></div>
          </div>
          <div class="mdc-form-field">
            <div class="mdc-checkbox">
              <input type="checkbox" class="mdc-checkbox__native-control" name="subscribe" id="email-on" />
              <div class="mdc-checkbox__background">
                <svg class="mdc-checkbox__checkmark" viewBox="0 0 24 24">
                  <path class="mdc-checkbox__checkmark-path" fill="none" d="M1.73,12.91 8.1,19.28 22.79,4.59"/>
                </svg>
                <div class="mdc-checkbox__mixedmark"></div>
              </div>
            </div>
            <label for="email-on">Send email alerts for new funds matching this search</label>
          </div>
          {{#if @root.request.params.query.keywords}}
            <input type="hidden" name="keywords" value="{{@root.request.params.query.keywords}}">
          {{/if}}
          {{#each (split "activities,beneficiaries,geographical_areas_funded,type_of_cost,type_of_funding,source_of_fund,miscellaneous" ",")}}
            {{#if (getProperty @root.request.params.query this)}}
              {{#each (arrayify (getProperty @root.request.params.query this))}}
                <input type="hidden" name="{{../this}}" value="{{this}}">
              {{/each}}
            {{/if}}
          {{/each}}
        </section>
        <footer class="mdc-dialog__actions">
          <button type="button" class="mdc-button mdc-dialog__button" data-mdc-dialog-action="close">Cancel</button>
          <button type="submit" class="mdc-button mdc-dialog__button" data-mdc-dialog-action="accept">Save</button>
        </footer>
      </form>
    </div>
    <div class="mdc-dialog__scrim"></div>
  </aside>
  {{/unless}}
{{/premium_content ~}}

<div class="mdc-layout-grid head v-margin">
  <div class="mdc-layout-grid__inner">
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-3-desktop mdc-layout-grid__cell--span-8-tablet mdc-layout-grid__cell--span-4-phone action">
      {{#if @root.data.auth ~}}
        {{#>premium_content}}
          {{#unless savedSearch ~}}
            {{#with (
              querystringify (obj)
              keywords=@root.request.params.query.keywords
              activities=(sort @root.request.params.query.activities)
              beneficiaries=(sort @root.request.params.query.beneficiaries)
              geographical_areas_funded=(sort @root.request.params.query.geographical_areas_funded)
              type_of_cost=(sort @root.request.params.query.type_of_cost)
              type_of_funding=(sort @root.request.params.query.type_of_funding)
              source_of_fund=(sort @root.request.params.query.source_of_fund)
              miscellaneous=(sort @root.request.params.query.miscellaneous)
            ) ~}}
            <button class="mdc-button mdc-button--raised mdc-button--large save-search hide-sm hide-md save-search-dialog-button" id="save-search-button">
              <span class="far fa-search-plus mdc-list-item__graphic" aria-hidden="true"></span>
              Save/email this search
            </button>
            {{/with ~}}
            <button class="mdc-button mdc-button--raised mdc-button--large filter-search hide-lg filter-search-button" id="filter-search-button">
              <span class="far fa-search mdc-list-item__graphic" aria-hidden="true"></span>
              Search filters
            </button>
          {{/unless ~}}
        {{/premium_content}}
        {{#>premium_content_alt}}
          <a href="/premium" class="mdc-button mdc-button--raised mdc-button--large save-search hide-sm hide-md" id="save-search-button">
            <span class="far fa-sign-in-alt mdc-list-item__graphic" aria-hidden="true"></span>
            Go premium to save searches and get alerts
          </a>
        {{/premium_content_alt}}
      {{else}}
        {{#compare @root.data.search_data.detailed_results.hits.total ">" 10 ~}}
          <a href="/sign-up" class="mdc-button mdc-button--raised mdc-button--large save-search hide-sm hide-md" id="save-search-button">
            <span class="far fa-sign-in-alt mdc-list-item__graphic" aria-hidden="true"></span>
            Sign up to see more results
          </a>
        {{/compare ~}}
        <button class="mdc-button mdc-button--raised mdc-button--large filter-search hide-lg filter-search-button" id="filter-search-button">
          <span class="far fa-search mdc-list-item__graphic" aria-hidden="true"></span>
          Search filters
        </button>
      {{/if ~}}
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-6-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone pagination-desktop">
      {{#if @root.data.auth ~}}
        {{> pagination_limited firstLastNavigation=true paging=@root.data.search_data.detailed_results.pagination}}
      {{/if ~}}
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-3-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
      {{#compare @root.data.search_data.detailed_results.hits.total ">" 0 ~}}
      <form action="{{{@root.request.url.path}}}" method="GET">
        {{#each (split "activities,beneficiaries,geographical_areas_funded,type_of_cost,type_of_funding,source_of_fund,miscellaneous" ",")}}
          {{#if (getProperty @root.request.params.query this) ~}}
            {{#each (arrayify (getProperty @root.request.params.query this)) ~}}
              <input type="hidden" name="{{../this}}[]" value="{{this}}" />
            {{/each ~}}
          {{/if ~}}
        {{/each ~}}
        {{#each (split "keywords" ",")}}
          {{#if (getProperty @root.request.params.query this) ~}}
            <input type="hidden" name="{{this}}" value="{{getProperty @root.request.params.query this}}" />
          {{/if ~}}
        {{/each ~}}
        {{> fund_search_sort}}
      </form>
      {{/compare ~}}
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-12-desktop mdc-layout-grid__cell--span-8-tablet mdc-layout-grid__cell--span-4-phone pagination-mobile">
      {{#if @root.data.auth ~}}
        {{> pagination_limited firstLastNavigation=true paging=@root.data.search_data.detailed_results.pagination}}
      {{/if ~}}
    </div>
  </div>
</div>

{{#if (contains @root.request.url.host "local:8080")}}
<div class="search-info mdc-elevation--z1">
  <h3 onclick="$('#search-query').toggleClass('hidden');">Search Query (show/hide)</h3>
  <pre id="search-query" class="hidden">{{{safeStringify @root.data.search_data.detailed_results.request}}}</pre>
