      {{#if onClasses}}
        "onClasses": {{{stringify onClasses}}},
      {{/if}}
      {{#if offClasses}}
        "offClasses": {{{stringify offClasses}}},
      {{/if}}
      "onText": "{{../onText}}",
      "offText": "{{../offText}}"}' role="button">
    {{#if ../onIcon}}
      <span class="{{../iconClasses}} {{if ../on ../onIcon else=../offIcon}}" aria-hidden="true"></span>
    {{/if}}
    <span class="toggle-link__text">
      {{{if ../on ../onText else=../offText}}}
    </span>
  </span>
{{/with}}

